import React, { useState } from 'react';
import axios from 'axios';

export default function AdminDashboard() {
  const [csvFile, setCsvFile] = useState(null);

  const handleUpload = async () => {
    if (!csvFile) return;
    const formData = new FormData();
    formData.append('file', csvFile);
    await axios.post('http://localhost:5000/api/results/upload', formData);
    alert('Results uploaded!');
  };

  return (
    <div className="flex">
      <nav className="w-64 bg-gray-100 min-h-screen p-4">
        <ul>
          <li className="mb-2 font-bold">Add/Edit Students</li>
          <li className="mb-2 font-bold">Upload Result</li>
          <li className="mb-2 font-bold">Manage Subjects</li>
          <li className="mb-2 font-bold">Manage Sessions/Terms</li>
          <li className="mb-2 font-bold">View Result History</li>
          <li className="mb-2 font-bold text-red-500 cursor-pointer" onClick={() => { localStorage.clear(); window.location = '/'; }}>Logout</li>
        </ul>
      </nav>
      <main className="flex-1 p-8">
        <h2 className="text-xl font-bold mb-4">ðŸ“¤ Upload Result for JSS2B - 2nd Term 2024/25</h2>
        <input type="file" accept=".csv" onChange={e => setCsvFile(e.target.files[0])} />
        <button className="bg-blue-500 text-white p-2 rounded ml-2" onClick={handleUpload}>Upload CSV</button>
        {/* Manual form and table can be added here */}
      </main>
    </div>
  );
}
