import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function StudentDashboard() {
  const [results, setResults] = useState([]);
  const [student, setStudent] = useState({});
  const [term, setTerm] = useState('2nd Term');
  const [session, setSession] = useState('2024/25');

  useEffect(() => {
    // Fetch student info and results
    const token = localStorage.getItem('token');
    if (!token) return;
    // Decode token to get student_id (for demo, hardcode)
    const student_id = '234567';
    setStudent({ fullname: 'John Doe', class: 'JSS2B', student_id });
    axios.get(`http://localhost:5000/api/student/${student_id}/result?term=${term}&session=${session}`)
      .then(res => setResults(res.data));
  }, [term, session]);

  const total = results.reduce((sum, r) => sum + Number(r.score), 0);
  const average = results.length ? (total / results.length).toFixed(1) : 0;

  return (
    <div className="p-8">
      <div className="mb-4">
        <h2 className="text-2xl font-bold">Welcome, {student.fullname} ({student.class})</h2>
        <div className="flex gap-4 mt-2">
          <span>ðŸ“˜ Session: {session}</span>
          <span>ðŸ“˜ Term: {term}</span>
        </div>
      </div>
      <table className="w-full border mb-4">
        <thead>
          <tr>
            <th className="border p-2">Subject</th>
            <th className="border p-2">Score</th>
            <th className="border p-2">Grade</th>
          </tr>
        </thead>
        <tbody>
          {results.map(r => (
            <tr key={r.subject}>
              <td className="border p-2">{r.subject}</td>
              <td className="border p-2">{r.score}</td>
              <td className="border p-2">{r.grade}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mb-4">
        <span className="mr-4">Total: {total}</span>
        <span className="mr-4">Average: {average}</span>
        <span className="mr-4">Position: 5th</span>
        <span>Remark: Excellent</span>
      </div>
      <button
        className="bg-green-500 text-white p-2 rounded"
        onClick={() => window.open(`http://localhost:5000/api/student/${student.student_id}/result/pdf?term=${term}&session=${session}`)}
      >
        ðŸ”½ Download Result as PDF
      </button>
      <button className="ml-4 bg-red-500 text-white p-2 rounded" onClick={() => { localStorage.clear(); window.location = '/'; }}>Logout</button>
    </div>
  );
}
